/**
 * Igual theme default functions
 */
add_action('after_switch_theme', 'igual_theme_default_setup_options');
function igual_theme_default_setup_options(){
	$default_options = igual_get_default_options();
	update_option( 'igual_options', json_decode( $default_options, true ) );
}

/**
* Enqueue Google Web Fonts.
*/
function igual_theme_default_fonts_url() {
   $font_url = '';
   
   /*
   Translators: If there are characters in your language that are not supported
   by chosen font(s), translate this to 'off'. Do not translate into your own language.
   
	*/
   $font_url = add_query_arg( 'family', urlencode( 'Marcellus:200,300,400,500,600,700|DM Sans:200,300,400,500,600,700,800,900&subset=latin' ), "//fonts.googleapis.com/css" );

   return $font_url;
}

